def limitar_tamanho_resposta(resposta, max_caracteres):
  # Verifique se a resposta excede o tamanho máximo permitido
  if len(resposta) > max_caracteres:
    # Truncar a resposta para o tamanho máximo permitido
    resposta_truncada = resposta[:max_caracteres]
    # Encontre a última ocorrência de um ponto final antes do ponto de truncagem
    ultimo_ponto = resposta_truncada.rfind('.')
    if ultimo_ponto != -1:
      resposta_truncada = resposta_truncada[:ultimo_ponto + 1]  # inclua o ponto final
    return resposta_truncada + "..."  # Adicione "..." para indicar que a resposta foi truncada
  else:
    return resposta


# Função para receber e processar respostas geradas pelo modelo
def processar_resposta_gerada(resposta_gerada):
  # Limite o tamanho da resposta
  resposta_processada = limitar_tamanho_resposta(resposta_gerada, 600)  # Limite a resposta a 600 caracteres
  # Outros processamentos necessários
  # ...
  return resposta_processada

# Função para enviar mensagem
def enviar_mensagem():
    # Obter a entrada do usuário
    user_input = entry.get()

    # Enviar mensagem
    response = convo.send_message(user_input)

    # Processar a resposta gerada
    resposta_processada = processar_resposta_gerada(response.text)

    # Adicionar entrada do usuário e resposta da TECA ao histórico
    conversation_history.insert(tk.END, "Você: " + user_input + "\n\n")
    conversation_history.insert(tk.END, "TECA: " + resposta_processada + "\n\n")
    conversation_history.insert(tk.END, "--------------------------------------------\n\n")
    conversation_history.see(tk.END)  # Rolagem automática para a última mensagem


# Criar a janela principal
root = tk.Tk()
root.title("T.E.C.A - Tecnologia de Eficiência Computacional para Atendimento - versão Alfa 1.0")
root.configure(background="orange")

# Criar uma caixa de entrada
entry = tk.Entry(root, width=50)
entry.pack(pady=10)

# Criar um botão para enviar mensagem
btn_send = tk.Button(root, text="Enviar", command=enviar_mensagem)
btn_send.pack(pady=5)

# Criar um histórico de conversa com uma altura maior e quebra de linha por palavras
conversation_history = tk.Text(root, width=100, height=30, wrap=tk.WORD)  # Ajuste a altura conforme necessário
conversation_history.pack(padx=10, pady=10)


# Executar o loop principal da janela
root.mainloop()