def limitar_tamanho_resposta(resposta, max_caracteres):
  # Verifique se a resposta excede o tamanho m√°ximo permitido
  if len(resposta) > max_caracteres:
    # Truncar a resposta para o tamanho m√°ximo permitido
    resposta_truncada = resposta[:max_caracteres]
    # Encontre a √∫ltima ocorr√™ncia de um ponto final antes do ponto de truncagem
    ultimo_ponto = resposta_truncada.rfind('.')
    if ultimo_ponto != -1:
      resposta_truncada = resposta_truncada[:ultimo_ponto + 1]  # inclua o ponto final
    return resposta_truncada + "..."  # Adicione "..." para indicar que a resposta foi truncada
  else:
    return resposta


# Fun√ß√£o para receber e processar respostas geradas pelo modelo
def processar_resposta_gerada(resposta_gerada):
  # Limite o tamanho da resposta
  resposta_processada = limitar_tamanho_resposta(resposta_gerada, 600)  # Limite a resposta a 600 caracteres
  # Outros processamentos necess√°rios
  # ...
  return resposta_processada

